int distance_in_cm = 30, LeftScan, CenterScan, RightScan, color1, color2;

void RaiseArm ()
{
  nMotorEncoder[motorA] = 0;
  while (SensorValue(armTouch) == 0)
  {
    motor[motorA] = 35;
  }
  motor[motorA] = 0;
  wait1Msec(100);
}

void LowerArm ()
{
  while(nMotorEncoder[motorA] > 0)
  {
    motor[motorA] = -30;
  }
  motor[motorA] = 0;
  wait1Msec(100);
}

void Drive_Forward (int speed1, int speed2)
{
  motor[motorB] = speed1;
  motor[motorC] = speed2;
}

