void Spin()
{
  while (SensorValue(sonarSensor) > 45)
    {
      //color1 = HTCSreadColor(colorSensor);
      CenterScan = SensorValue(sonarSensor);
      Drive_Forward(25, -25);
    }
  wait1Msec(200);
  Drive_Forward(0, 0);
}

